import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../servicios/api';

const InicioSesion = ({ setAutenticado }) => {
    const [credenciales, setCredenciales] = useState({ email: '', password: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredenciales({ ...credenciales, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Enviando:', credenciales); // üõ†Ô∏è Verifica los datos en la consola del navegador

        try {
            const response = await api.post('/login', credenciales);

            console.log('Respuesta del servidor:', response.data); // üõ†Ô∏è Muestra la respuesta en la consola

            if (response.data && response.data.token) {
                localStorage.setItem('token', response.data.token);
                setAutenticado(true);
                alert(`Inicio de sesi√≥n exitoso. ¬°Bienvenido(a) ${credenciales.email}!`);
                navigate('/home');
            } else {
                throw new Error('No se recibi√≥ un token');
            }
        } catch (error) {
            console.error('Error en el login:', error.response?.data); // üõ†Ô∏è Verifica el error en consola
            alert(error.response?.data?.message || 'Error al iniciar sesi√≥n, por favor verifica tus credenciales');
        }
    };


    return (
        <div className="container">
            <h2>Iniciar Sesi√≥n</h2>
            <form onSubmit={handleSubmit} className="auth-form">
                <input 
                    type="email" 
                    name="email" 
                    placeholder="Email" 
                    value={credenciales.email} 
                    onChange={handleChange} 
                    required 
                />
                <input 
                    type="password" 
                    name="password" 
                    placeholder="Password" 
                    value={credenciales.password} 
                    onChange={handleChange} 
                    required 
                />
                <button type="submit">Iniciar Sesi√≥n</button>
            </form>
        </div>
    );
};

export default InicioSesion;
