import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../servicios/api';

const InicioSesion = ({ setAutenticado }) => {
    const [credenciales, setCredenciales] = useState({ nombre: '', identificador: '', password: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredenciales({ ...credenciales, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post('/login', credenciales);
            localStorage.setItem('token', response.data.token);
            setAutenticado(true);
            alert(`Inicio de sesión exitoso. ¡Bienvenido(a) ${credenciales.nombre}!`);
            navigate('/');
        } catch (error) {
            alert('Error al iniciar sesión');
        }
    };

    return (
        <div className="auth-container">
            <h2>Iniciar Sesión</h2>
            <form onSubmit={handleSubmit} className="auth-form">
                <input type="text" name="nombre" placeholder="Nombre" value={credenciales.nombre} onChange={handleChange} required />
                <input type="text" name="identificador" placeholder="Email" value={credenciales.identificador} onChange={handleChange} required />
                <input type="password" name="password" placeholder="Pasword" value={credenciales.password} onChange={handleChange} required />
                <button type="submit">Iniciar Sesión</button>
            </form>
        </div>
    );
};

export default InicioSesion;
