import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import css from '../css/'
import api from '../app';

const Registro = () => {
    const [usuario, setUsuario] = useState({ nombre: '', email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        setUsuario({ nombre: '', email: '', password: '' });
    }, []);

    const handleChange = (e) => {
        setUsuario({ ...usuario, [e.target.name]: e.target.value });
    };

    const validarFormulario = () => {
        if (!usuario.nombre.trim()) {
            alert('El nombre es obligatorio');
            return false;
        }
        if (!usuario.email.includes('@')) {
            alert('Correo electrónico no válido');
            return false;
        }
        if (usuario.password.length < 6) {
            alert('La contraseña debe tener al menos 6 caracteres');
            return false;
        }
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validarFormulario()) return;

        setLoading(true);
        try {
            await api.post('/register', usuario);
            alert('Usuario registrado correctamente');
            navigate('/login');
        } catch (error) {
            alert(error.response?.data?.message || 'Error al registrar usuario');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <h2>Registro</h2>
            <form onSubmit={handleSubmit} className="auth-form">
                <input type="text" name="nombre" placeholder="Nombre" value={usuario.nombre} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Correo Electrónico" value={usuario.email} onChange={handleChange} required />
                <input type="password" name="password" placeholder="Contraseña" value={usuario.password} onChange={handleChange} required />
                <button type="submit" disabled={loading}>
                    {loading ? 'Registrando...' : 'Registrarse'}
                </button>
            </form>
        </div>
    );
};

export default Registro;
