import React, { useState } from 'react';
import { TextField, Button, Grid2, Box } from '@mui/material';
import { useN } from 'react-router-dom';
import api from '../contexto/AutenticacionContexto';


const InicioSesion = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { setAuth } = useState('');
    const history = useHistory('');

    const handleSubmit = async (e) => {
        e.preventdefault();
        try {
            const response = await api.post('/auth/login', { email, password });
            setAuth(response.data.token);
            history.push('/')
        }catch (error) {
            console.error("Error de inicio de sesion", error);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit}>
            <Grid2 container spacing={2}>
                <Grid2 item xs={12}>
                    <TextField
                        label="Correo Electronico"
                        fullWidth
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </Grid2>
                <Grid2 item xs={12}>
                    <TextField
                        label="ContraseÃ±a"
                        type="password"
                        fullWidth
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </Grid2>
                <Grid2 item xs={12}>
                    <Button type="submit" variant="contained" color="primary">Iniciar Sesion</Button>
                </Grid2>
            </Grid2>
        </Box>
    );
}

export default InicioSesion;
