export const AuthProvider = ({ children }) => {
    const [usuario, setUsuario] = useState(null);

    const register = (userData) => setUsuario(userData);

    // ✅ Corrección: login debe recibir datos y llamar a setUsuario correctamente
    const login = (userData) => {
        setUsuario(userData); // Guarda el usuario en el contexto
    };

    return (
        <AuthContext.Provider value={{ usuario, register, login }}>
            {children}
        </AuthContext.Provider>
    );
};
