import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import './css/global.css';
import { AuthProvider, useAuth } from '../src/contexto/AutenticacionContexto';

import Inicio from '../src/paginas/Inicio';
import Eventos from '../src/paginas/Eventos';
import CrearEvento from '../src/paginas/CrearEvento';
import EditarEvento from '../src/paginas/EditarEvento';
import InicioSesion from '../src/componentes/InicioSesion';
import Registro from '../src/componentes/Registro';

// Componente para proteger rutas privadas
const RutaProtegida = ({ element }) => {
    const { usuario } = useContext(AuthContext);
    return usuario ? element : <Navigate to="/login" />;
};

const App = () => {
    return (
        <AuthProvider>
            <Router>
                {/* Barra de navegación */}
                <nav className="navbar">
                    <Link to="/login">INICIAR SESIÓN</Link>
                    <Link to="/register">REGISTRO</Link>
                    <Link to="/">INICIO</Link>
                    <Link to="/eventos">EVENTOS</Link>
                    <Link to="/crear-evento">CREAR EVENTO</Link>
                </nav>

                {/* Contenido principal */}
                <div className="welcome-message">
                    Bienvenido a la Aplicación Gestión de Eventos
                </div>

                {/* Rutas de autenticación */}
                <Routes>
                    <Route path="/login" element={<InicioSesion />} />
                    <Route path="/register" element={<Registro />} />

                    {/* Rutas protegidas (se acceden solo si el usuario ha iniciado sesión) */}
                    <Route path="/" element={<RutaProtegida element={<Inicio />} />} />
                    <Route path="/eventos" element={<RutaProtegida element={<Eventos />} />} />
                    <Route path="/crear-evento" element={<RutaProtegida element={<CrearEvento />} />} />
                    <Route path="/editar-evento/:id" element={<RutaProtegida element={<EditarEvento />} />} />
                </Routes>
            </Router>
        </AuthProvider>
    );
}

export default App;
