import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../servicios/api';
import '../css/global.css';

const Registro = () => {
    const [usuario, setUsuario] = useState({ nombre: '', email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => setUsuario({ ...usuario, [e.target.name]: e.target.value });

    const validarFormulario = () => {
        if (!usuario.nombre.trim()) return alert('El nombre es obligatorio');
        if (!usuario.email.includes('@')) return alert('Correo inválido');
        if (usuario.password.length < 6) return alert('La contraseña debe tener al menos 6 caracteres');
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validarFormulario()) return;
        setLoading(true);
        try {
            await api.post('/register', usuario);
            alert('Usuario registrado correctamente');
            navigate('/login');
        } catch (error) {
            alert(error.response?.data?.message || 'Error en el registro');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <nav className="navbar"><span>Bienvenido a Gestión de Eventos</span></nav>
            <div className="container">
                <h2>Registro</h2>
                <form onSubmit={handleSubmit}>
                    {['nombre', 'email', 'password'].map((campo) => (
                        <input key={campo} type={campo === 'email' ? 'email' : 'text'} name={campo}
                            placeholder={campo} value={usuario[campo]} onChange={handleChange} required />
                    ))}
                    <button type="submit" disabled={loading}>{loading ? 'Registrando...' : 'Registrarse'}</button>
                </form>
            </div>
        </div>
    );
};

export default Registro;
