import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../servicios/api';

const Registro = () => {
    const [usuario, setUsuario] = useState({ nombre: '', email: '', password: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setUsuario({ ...usuario, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await api.post('/register', usuario);
            alert('Usuario registrado correctamente');
            navigate('/login');
        } catch (error) {
            alert('Error al registrar usuario');
        }
    };

    return (
        <div className="auth-container">
            <h2>Registro</h2>
            <form onSubmit={handleSubmit} className="auth-form">
                <input type="text" name="nombre" placeholder="Nombre" value={usuario.nombre} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Correo Electrónico" value={usuario.email} onChange={handleChange} required />
                <input type="password" name="password" placeholder="Contraseña" value={usuario.password} onChange={handleChange} required />
                <button type="submit">Registrarse</button>
            </form>
        </div>
    );
};

export default Registro;
